  const [code, setCode] = useState();

  const handleSubmitConfirm = (e) => {
    e.preventDefault();
    axios
      .post(
        " https://auth.enterprise.wikimedia.com/v1/forgot-password-confirm",
        {
          username: user,
          password: pwd,
          confirmation_code: code,
