import React, { useEffect, useRef, useState } from "react";
import axios from "axios";

function ForgotPassword() {
  // Setting focus on user input field.
  const userRef = useRef();

  const [user, setUser] = useState("");
  const [pwd, setPwd] = useState();
  const [code, setCode] = useState();

  const [userFocus, setUserFocus] = useState(false);

  useEffect(() => {
    userRef.current.focus();
  }, []);

  const [errMsg, setErrMsg] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    axios
      .post("https://auth.enterprise.wikimedia.com/v1/forgot-password", {
        username: user,
      })
      .then((reponse) => {
        console.log(reponse.data);
      })
      .catch((err) => {
        console.log(err);
        console.log(err.reponse);
      });
    setUser("");
    setSuccess(true);
  };

  const handleSubmitConfirm = (e) => {
    e.preventDefault();
    axios
      .post(
        " https://auth.enterprise.wikimedia.com/v1/forgot-password-confirm",
        {
          username: user,
          password: pwd,
          confirmation_code: code,
        }
      )
      .then((reponse) => {
        console.log(reponse.data);
        alert("password successfully change!");
      })
      .catch((err) => {
        console.log(err);
        console.log(err.reponse);
      });
    setUser("");
    setSuccess(true);
  };

  return (
    <>
      {success ? (
        <section>
          <h1> New Password </h1>
          <br />
          <h5>
            {" "}
            Please enter a new password & confirmation code sent in your E-mail
            adress.{" "}
          </h5>
          <br />

          <form onSubmit={handleSubmitConfirm}>
            <label htmlFor="username">Username :</label>
            <input
              type="text"
              placeholder="please enter username"
              id="username"
              ref={userRef}
              autoComplete="off"
              onChange={(e) => setUser(e.target.value)}
              value={user}
              required
            />

            <label htmlFor="password"> New password :</label>
            <input
              type="text"
              placeholder="please enter new password"
              id="password"
              autoComplete="off"
              onChange={(e) => setPwd(e.target.value)}
              value={pwd}
              required
            />

            <label htmlFor="code">code :</label>
            <input
              type="code"
              placeholder="please enter code"
              id="code"
              autoComplete="off"
              onChange={(e) => setCode(e.target.value)}
              value={code}
              required
            />
          </form>
          <button href="#"> Submit </button>
        </section>
      ) : (
        <section>
          <p className={errMsg ? "errmsg" : "offScreen"} aria-live="assertive">
            {errMsg}
          </p>
          <h1>Forgot Password</h1>
          <br />
          <h5> Enter your Username </h5>

          <form onSubmit={handleSubmit}>
            <label htmlFor="username">Username :</label>
            <input
              type="text"
              placeholder="Please enter username"
              id="username"
              ref={userRef}
              autoComplete="off"
              onChange={(e) => setUser(e.target.value)}
              value={user}
              required
            />

            <button>Continue</button>
          </form>
        </section>
      )}
    </>
  );
}

export default ForgotPassword;
